mapper.insert:
INSERT INTO ProductImg (
    product_Idx,
    product_Img,
    product_Img_Order
)
VALUES (
    #{productIdx},
    #{productImg},
    #{productImgOrder}
)

INSERT INTO ProductDescImg (
    product_Idx,
    product_Desc_Img,
    product_Desc_Img_Order
)
VALUES (
    #{productIdx},
    #{productDescImg},
    #{productDescImgOrder}
)

sql.where:
AND reg_date BETWEEN #{startDate} AND #{endDate}        

AND INSTR(product_Idx, #{keyword}) > 0


AND INSTR(product_Name, #{keyword}) > 0
